PcDocRulesExpr.LValue:
/*== OrderDtl-v6.1.0 - DocRule Action ========================================

	Created: 07/18/2022 -Kevin Veldman
	Changed: 01/19/2023 -KV

	Info: Clean/Set defaults for missing data; Get calculated values; Set Order Line fields;
	File: SMO-DocRules-OrderDtl-v6.1.0.cs
============================================================================*/

	string sKey = OrderDtl.cDeviceCode_c;

	UDMethods.drSetFinalData(sKey); // Clean up data and set calculated values

	Func<string,string,string,string> sLkp = (t,c,r) => PCLookUp.DataLookup(t,c,r);
	Func<string,string> mySpec = c => sLkp("DefaultSpecs", c, sKey);

	Func<string,decimal,bool> kStrDec = (s,d) => decimal.TryParse(s, out d); 
	Func<string,int,    bool> kStrInt = (s,i) => int.TryParse(s, out i); 

	Func<decimal,string> sDec = d => d.ToString(); // works with Int32
	Func<string,decimal> dStr = s => kStrDec(s,0)? Convert.ToDecimal(s): 0;
	Func<string,int    > iStr = s => kStrInt(s,0)? Convert.ToInt32(s)  : 0;
	Func<string,bool   > kStr = s => ( s == "1" || s.ToLower() == "true");

	int custNum = Context.CustomerNumber;
	string mod   = OrderDtl.ModType_c;
	decimal dQty = OrderDtl.cSBLR_c=="B"? 2: 1;
	bool kTOP = (custNum == 1110), kHPO = (custNum == 1007);
	bool kRush = OrderDtl.kRush0D_c || OrderDtl.kRush1D_c;
	string basePN = ( OrderDtl.BasePartNum!=""   ) ? OrderDtl.BasePartNum :
	                ( OrderDtl.PartNum=="webSMO" ) ? "webSMO" : "cfgSMO";

	string[] ProdDates = UDMethods.drGetShopDates(sKey);
	string[] PriceDtl  = UDMethods.drGetPriceDtl(sKey);

	bool kRmkNC = UDMethods.drRemakeNC(OrderDtl.kRemake_c, kTOP);
	var dUnitPrice = kRmkNC? 0.00m: dStr(PriceDtl[0]);
	var dDiscount  = kRmkNC? 0.00m: dStr(PriceDtl[2]);
	var dDiscRate  = kRmkNC? 0.00m: dStr(PriceDtl[3]);

	StringBuilder dsc = new StringBuilder();
	var sMod = " from " + (mod=="C"? "Cast": mod=="S"? "Scan": "Measurements");
	dsc.Append(mySpec("DESC"));
	dsc.Append(kTOP? sMod: ", Custom Molded");
	dsc.Append(OrderDtl.kPrepped_c? ", Prepped": "");


// Set Product Group, Price, Quantity, Rush Status, Discount, PartNum.
	OrderDtl.BasePartNum     = basePN;
	OrderDtl.BaseRevisionNum = OrderDtl.RevisionNum;
	OrderDtl.PartNum         = UDMethods.getNewPart(sKey);
	OrderDtl.LineDesc        = dsc.ToString();

	OrderDtl.SellingQuantity = dQty;
	Pricing.OrderPrice       = dUnitPrice;
	OrderDtl.OrderComment    = dUnitPrice > 0? PriceDtl[1]: "";
	
	OrderDtl.DocDiscount     = dDiscount * dQty;
	
	if (dDiscRate > 0) 
		OrderDtl.DiscountPercent = dDiscRate;

	OrderDtl.LockDisc = true;

	OrderDtl.MktgEvntSeq = kRush? 2: 1;
	OrderDtl.RequestDate = DateTime.Parse(ProdDates[0]); //Turnaround
	OrderDtl.NeedByDate  = DateTime.Parse(ProdDates[1]); //Promised

	OrderDtl.ProdCode = sLkp("Standards", "Value", mySpec("BRAND").ToUpper());


// Set Tariff Code for International Orders only
	string[] nonIntl = PCLookUp.DataColumnList("dmstcGrps","Groups").Split('~');
	string custGrp = ( kTOP || kHPO )? "INC" : UDMethods.getCustGrp(custNum);
	
	if (Array.IndexOf(nonIntl, custGrp) < 0) OrderDtl.cIntlCustomsCode_c = "90211090";


// Set OrderHed Dates
	if (kRush) OrderHed.NeedByDate  = OrderDtl.NeedByDate;
	if (kRush) OrderHed.RequestDate = OrderDtl.RequestDate;
	
	if(OrderHed.NeedByDate==null || OrderHed.NeedByDate < OrderDtl.NeedByDate)
		OrderHed.NeedByDate = OrderDtl.NeedByDate;

	if(OrderHed.RequestDate==null || OrderHed.RequestDate<OrderDtl.RequestDate)
		OrderHed.RequestDate = OrderDtl.RequestDate;

// Set OrderDate, review-hold when generated w/out using client configurator
	if (!Inputs.kClientRules.Value) OrderDtl.kPartTrap_c = true;



/*== CHANGE LOG =============================================================

	09/15/2022: Online order rules: hold for review + wait for cast/scan;
	09/26/2022: TOP orders to include ModType in LineDesc; update BaseRevisionNum;
	11/30/2022: Refactor w/ combined UDMethods reduce # of queries executed;
	12/06/2022: Unblock Method Rules with client input. Prevents early execution errors;
	12/22/2022: New function returning string[] for dates. 
	01/02/2023: Fix syntax errors, ProdCode lookup;
	01/09/2023: Remove query from drRemakeNC, Remove kRush condition from getShopDates;
	01/09/2023: Check Hanger/TOP before querying for CustGrp, do not empty RevisionNum;
	01/11/2023: Always set BasePart to "webSMO", replace prepDocRules w/ drSetFinalData;
	01/19/2023: Fix issue with no-addition braces getting default CustGroup level discount;

============================================================================*/

/*== QuoteDtl-v4.0.2 - DocRule Action ========================================

	Created: 01/02/2023 -Kevin Veldman
	Changed: 01/23/2023 -KV

	Info: Clean/Set defaults for missing data; Get calculated values; Set Quote Line fields;
	File: SMO-DocRules-QuoteDtl-v4.0.2.cs
============================================================================*/

	string sKey = QuoteDtl.cDeviceCode_c;

	UDMethods.drSetFinalData(sKey); // Clean up data and set calculated values

// Variables 
	Func<string,string> mySpec = c => UDMethods.sLkp("DefaultSpecs", c, sKey);
	string mod = QuoteDtl.ModType_c, sBrand = mySpec("BRAND").ToUpper();
	decimal dQty = QuoteDtl.cSBLR_c=="B"? 2: 1;
	var isTop = Context.CustomerNumber == 1110;
	string basePN = ( OrderDtl.BasePartNum!=""   ) ? OrderDtl.BasePartNum :
	                ( OrderDtl.PartNum=="webSMO" ) ? "webSMO" : "cfgSMO";

	bool kRmkNC = UDMethods.drRemakeNC(QuoteDtl.kRemake_c, isTop);
	string[] PriceDtl = UDMethods.drGetPriceDtl(sKey);

	var dUnitPrice = kRmkNC? 0.00m: Convert.ToDecimal(PriceDtl[0]);
	var dDiscount  = kRmkNC? 0.00m: Convert.ToDecimal(PriceDtl[2]);
	var dDiscRate  = kRmkNC? 0.00m: Convert.ToDecimal(PriceDtl[3]);

	StringBuilder dsc = new StringBuilder();
	var dscTop = " from " +(mod=="C"?"Cast":(mod=="S"?"Scan":"Measurements"));
	dsc.Append(mySpec("DESC")).Append(isTop? dscTop: ", Custom Molded");
	dsc.Append(QuoteDtl.kPrepped_c? ", Prepped": "");


//Set Product Group, Price, Quantity, Rush Status.
	QuoteDtl.BasePartNum        = basePN;
	QuoteDtl.BaseRevisionNum    = QuoteDtl.RevisionNum;
	QuoteDtl.PartNum            = UDMethods.getNewPart(sKey);
	//QuoteDtl.RevisionNum      = "";
	Pricing.QuotePrice          = dUnitPrice;
	QuoteDtl.DocExpUnitPrice    = dUnitPrice;
	QuoteDtl.SellingExpectedQty = dQty;
	QuoteDtl.OrderQty           = dQty;
	QuoteDtl.LineDesc           = dsc.ToString();
	QuoteDtl.QuoteComment       = dUnitPrice > 0? PriceDtl[1]: ""; 
	QuoteDtl.ProdCode           = UDMethods.sLkp("Standards","Value",sBrand);

	if (dDiscount > 0) QuoteDtl.DocDiscount = dDiscount * dQty;
	if (dDiscRate > 0) QuoteDtl.DiscountPercent = dDiscRate;

// Set Tariff Code for International Quotes only
	string[] nonIntl = PCLookUp.DataColumnList("dmstcGrps","Groups").Split('~');
	string custGroup = isTop || Context.CustomerID=="HANGER"? "INC": UDMethods.getCustGrp(Context.CustomerNumber);

	if (Array.IndexOf(nonIntl, custGroup) < 0)
		QuoteDtl.cIntlCustomsCode_c = "90211090";

// Set OrderDate, review-hold when generated w/out using client configurator
	if (!Inputs.kClientRules.Value) QuoteDtl.kPartTrap_c = true;



/*== CHANGE LOG ==============================================================
	11/15/2022: Additional unit price and quantity fields set;
	01/02/2023: Copy and adapt from OrderDtl 5.2.2;
	01/09/2023: Remove query from drRemakeNC (OrderDtl v5.3.0);
	01/23/2023: Add drGetPriceDtl UDMethod;
============================================================================*/

/*== JobHead-v1.4.0 ==========================================================

	Created: 08/09/2022 -Kevin Veldman
	Changed: 01/02/2023 -KV

	Info: Set Modification Deadline date ( JobHead.UserDate4 )
	File: SMO-DocRules-JobHead-v1.4.0.cs
============================================================================*/

DateTime ModDate = Convert.ToDateTime(JobHead.CreateDate ?? DateTime.Today);

Action<int> DateCheck = (iDays) => {

	string[] Holiday = PCLookUp.DataColumnList("holidays","dates").Split('~');
	ModDate = ModDate.AddDays(iDays);

	for (int i = 0; i < Holiday.Length; i++) {
		if (Holiday[i] == ModDate.ToString("yyyyMMdd")) ModDate = ModDate.AddDays(1);
	}

	if (ModDate.DayOfWeek == DayOfWeek.Friday  ) ModDate = ModDate.AddDays(3);
	if (ModDate.DayOfWeek == DayOfWeek.Saturday) ModDate = ModDate.AddDays(2);
	if (ModDate.DayOfWeek == DayOfWeek.Sunday  ) ModDate = ModDate.AddDays(1);
};

Action<int> plusDays = (iDays) => {
	for (int i = 0; i < iDays; i++){
		DateCheck(1);
		DateCheck(0);
	}
};

	bool doMods = OrderDtl.ModType_c != "M" || OrderDtl.dMeas5_c > 3.125m;

	int daysAdded = ( !doMods || OrderDtl.kRush0D_c ) ? 0: 
	                ( OrderDtl.kRush1D_c            ) ? 1: 2 ;

	plusDays( daysAdded );


JobHead.UserDate4 = ModDate;



/*== CHANGE LOG ==============================================================
	09/14/2022: Add ProdQty, CreateDate, JobEngineered, JobReleased;
	09/15/2022: Revert to v1, extra JobHead fields unnecessary;
	01/02/2023: Minor refactoring; 
============================================================================*/

/*== OrderHed-v1.1.0 =========================================================

	Created: 09/20/2022 -Kevin Veldman
	Changed: 09/21/2022 -KV

	Info: Set order to hold for online when criteria are met. 
	File: SMO-DocRules-OrderHed-v1.1.0.cs
============================================================================*/

	if (!Inputs.kClientRules.Value) {

		OrderHed.OrderDate = Convert.ToDateTime(DateTime.Today);

		if (OrderDtl.OrderNotes_c.Length > 0) OrderHed.OrderHeld = true;
		if (OrderDtl.kRemake_c              ) OrderHed.OrderHeld = true;
		if (OrderDtl.ModType_c != "M"       ) OrderHed.OrderHeld = true;
		
		if (OrderHed.OrderHeld) OrderHed.dtOnHold_c = OrderHed.OrderDate;
	}



/*== CHANGE LOG ==============================================================
	09/21/2022: Fixed bug: Hold was being overridden by next line;
============================================================================*/

PcFunctionDef.Expression:
/*== calcPriceDetail v1.0.1 ==================================================

	Created: 01/13/2023 -Kevin Veldman
	Changed: 01/19/2023 -KV
	
	File: SMO-UDM_c-calcPriceDtl-v1.0.1.cs
	Info: Set UnitPrice, SO Comment, Discount Values - CLIENT INPUTS ONLY
		!! MUST MANUALLY KEEP IN SYNC WITH UDMethods.getPriceDtl !!
============================================================================*/

/*== Global Functions & Variables ==========================================*/
	Func<string,bool> kpVal = s => ( s!="" && s.ToUpper()!="NONE" );
	Func<string,decimal,bool> kStrDec = (s,d) => decimal.TryParse(s, out d); 
	Func<string,decimal> dStr  = s => kStrDec(s,0)? Convert.ToDecimal(s): 0;
	Func<decimal,string> sDec = d => d.ToString();

	Func<string,string,string,string> sLkp = (t,c,r) => PCLookUp.DataLookup(t,c,r);
	Func<string,string> lkSpc = s => sLkp("DefaultSpecs", s, sKey);	
	Func<string,string,string,decimal> dLkp = (t,c,r) => dStr(sLkp(t,c,r));

	Func<string[],string,bool> sAIO = (a,s) => ( Array.IndexOf(a,s) >= 0 );
	Func<string,string,string> lsRow = (t,r) => PCLookUp.DataRowList(t,r);
	Func<string,string,string> lsCol = (t,c) => PCLookUp.DataColumnList(t,c);


	int custNum = Context.CustomerNumber;
	var custID  = Context.CustomerID;
	var custGrp = getCustGrp(custNum);
	var shipTo  = getShipTo(Convert.ToInt32(Inputs.dOrderNum.Value));

	if ( Context.Entity == "PcStatus" ) {
		if ( Inputs.cCustID.Value == "HANGER" ) {
			custNum = 1007;
			custID = "HANGER";
			custGrp = "INC";
			shipTo = "TOP SB";
		} else if ( Inputs.cCustID.Value == "EUR" ) {
			custGrp = "EUR";
		}
	}

	decimal dQty = Inputs.rQty.Value=="B"? 2: 1;
	bool kRush = Inputs.kRush.Value;
	bool kBoot = Inputs.kInnerBoot.Value && lkSpc("SPEC") != "BIGSHOT";
/*==========================================================================*/

/*== UnitPrice & Itemized OrderComment =====================================*/

	//-- Set Price & Desc Variables ------------------------------------
		StringBuilder smo = new StringBuilder();

		string[] Price = lsRow("PriceMaster", sKey  ).Split('~');
		string[] Desc  = lsRow("PriceMaster", "Desc").Split('~');
		string[] Dmstc = lsCol("dmstcGrps", "Groups").Split('~');

		Func<int,decimal> drPrc = i => dStr(Price[i]);

		decimal prcAlt  = dLkp("AltBasePrices", custGrp, sKey);
		decimal prcBase = prcAlt > 0? prcAlt: drPrc(3) + drPrc(4);
		decimal prcUnit = 0;

		decimal prcPrep = dLkp("Standards", "Value", "Prep") * -1;
		string  dscPrep = sLkp("Standards", "Desc" , "Prep");

		string  rushRow = (Inputs.k0day.Value? "0": "1") + "RushSMO";
		decimal prcRush = Inputs.kNCRush.Value?0:dLkp("Standards","Value",rushRow);
		string  dscRush = sLkp("Standards","Desc",rushRow);

		decimal dWdg = Inputs.dWedgeQty.Value;
		bool   kPrep = Inputs.rFinish.Value=="P";
	//-------------------------------------------------------------------


	//-- Functions to add lines & increment price -----------------------
		Action<int,bool> addLn0 = (i,k) => {
			if ( k ) {
				smo.Append( Desc[i].PadRight(28) );
				smo.Append( dQty.ToString() );
				smo.Append( Price[i].PadLeft(10) );
				smo.Append( (dQty*drPrc(i)).ToString().PadLeft(11) );
				smo.AppendLine();

				prcUnit += drPrc(i);
			}
		};

		Action<string,bool,decimal,decimal,int> addLn1 = 
			(sDesc,Keep,Qty,dPrc,iFixed) => { //Custom Price|Qty|Desc|Fixed Params
			
			if ( Keep ) {
		
				decimal myQty = iFixed==1? Qty:(Qty*dQty);

				smo.Append( sDesc.PadRight(28) );
				smo.Append( myQty.ToString() );
				smo.Append( dPrc.ToString().PadLeft(10) );
				smo.Append( (myQty*dPrc).ToString().PadLeft(11) );
				smo.AppendLine();
				
				prcUnit += ((dPrc*Qty)/(iFixed==1? dQty: 1));
			}
		}; 
	//-------------------------------------------------------------------


	//-- Set Title + Charge + Subtotal Lines ----------------------------
		smo.Append("Description".PadRight(27));
		smo.Append("Qty");
		smo.Append("UnitPrice".PadLeft(10));
		smo.Append("ExtPrice".PadLeft(11));
		smo.AppendLine();


		addLn1( Price[0], true, 1, prcBase, 0 );        //Base Device
		addLn0( 11, Inputs.kToeWalk.Value     );        //Toe Walker Mods
		addLn0( 18, Inputs.kSpringPlate.Value );        //Spring Steel Soles
		addLn0( 17, Inputs.kCarbonPlate.Value );        //Carbon Fiber Soles
		addLn0( 21, Inputs.kNonSkid.Value     );        //Non-Skid Soles
		addLn0( 19, Inputs.kDorsalChip.Value  );        //Dorsal Chips
		addLn0( 10, kBoot );                            //Inner Boot
		addLn0( 12, Inputs.kLiner.Value   );            //Integrated Liner
		addLn0( 5,  Inputs.kSTPad.Value   );            //ST Pads
		addLn0( 6,  Inputs.kMetPad.Value  );            //Metatarsal Pads
		addLn0( 31, Inputs.kPlateau.Value );            //Toe Plateaus
		addLn0( 32, Inputs.kCutout.Value  );            //Extra Cutouts
		addLn1( Desc[33], dWdg>0, dWdg, drPrc(33), 1 ); //Heel Wedge
		addLn0( 35, Inputs.kAppScanFee.Value  );        //Scan Submission Fee
		addLn1( dscPrep, kPrep, 1, prcPrep, 0 );        //Prep Discount
		addLn1( dscRush, kRush, 1, prcRush, 1 );        //Rush Charge


		smo.AppendLine();
		smo.Append("Total Price".PadRight(28));
		smo.Append(sDec(dQty));
		smo.Append(sDec(prcUnit).PadLeft(10));
		smo.Append(sDec(dQty * prcUnit).PadLeft(11));
	//-------------------------------------------------------------------


	/*-- PriceMaster Table Column-Index Reference -----------------------
		0	Desc1
		1	HCPSC
		2	Code
		3	BASE
		4	STD ADD
		5	STPad
		6	MetPad
		7	tlsoGills
		8	tlsoGusset
		9	tlsoKIEU
		10	kInnerBootOpt
		11	kToeWalk
		12	kLinerOpt
		13	kTStrap
		14	kAntShellOpt
		15	kAntShellOpt2
		16	kDiabeticInsert
		17	kCarbonPlate
		18	kSpringPlate
		19	kDorsalChips
		20	kCarbonSupport
		21	kNonSkid
		22	kWalkingBase
		23	kNightStretch
		24	kQuickRelease
		25	kPowderCoat
		26	kUpgrade
		27	CarbonFiberShank
		28	RGOBucket
		29	HKAFOPelvicBand
		30	AFOFCLift
		31	kPlateau
		32	kBigShotHoles
		33	kWedge
		34	kExtraMods
		35	kAppScanFee 
	-------------------------------------------------------------------*/
/*==========================================================================*/




/*== Discounts on Configured Pricing =======================================*/

	//-- Local Functions & Variables ----------------------------------------
		string[]   dscID = lsCol("uniqueDiscounts","discountID").Split('~');
		string[] dscShip = lsCol("uniqueDiscounts","shipToID"  ).Split('~');
		string[] dscName = lsCol("uniqueDiscounts","custID"    ).Split('~');
		string[]  dscGrp = lsCol("uniqueDiscounts","custGrp"   ).Split('~');
		string[]  dscVal = lsCol("uniqueDiscounts","discAmnt"  ).Split('~');
		string[] dscRate = lsCol("uniqueDiscounts","discRate"  ).Split('~');

		decimal dscValCust=0,  dscValShip=0,  dscValGroup=0;
		decimal dscRateCust=0, dscRateShip=0, dscRateGroup=0;
	//-------------------------------------------!! DO NOT CHANGE !!---------


	//-- Set Condition/Multiplier for each discountID -----------------------
		decimal coupaSAI2  = 0;
		decimal coupaHAI2  = 0;
		decimal coupaNSkd  = 0; //Inputs.kNonSkid.Value? 1: 0;
		decimal coupaCFFP  = 0; //Inputs.kCarbonPlate.Value? 1: 0;
		decimal coupaLiner = 0; //Inputs.kLiner.Value? 1: 0;
		decimal coupaBoot  = 0; //kBoot? 1: 0;
		decimal coupaDAJ   = 0;
		decimal coupaTAM   = 0;
		decimal coupaChip  = 0; //Inputs.kDorsalChip.Value && lkpSpecs("ChipsIncluded")!="X"? 1: 0;
		decimal top22      = 1;
		decimal ottoWRI    = 1;
		decimal ottoSAR    = 1;
		decimal ottoABIL   = 1;
		decimal ottoACT    = 1;
		decimal ottoJPO    = 1;
		decimal nuaeBoot   = kBoot? 1: 0;
		decimal spineGOSS  = 0;  // TLSO Only
		decimal coupaPLI2  = 0;
		decimal coupaART   = 0;
		decimal coupaSSA   = 0;
		decimal eurSTAB    = 0; //Stabilizer only
		decimal eurLiner   = Inputs.kLiner.Value?1:0;
		decimal eurNSkd    = Inputs.kNonSkid.Value?1:0;
		decimal eurBoot    = kBoot? 1: 0;
		decimal eurWBase   = 0;
		decimal coupaTWMod = 0; //Inputs.kToeWalk.Value? 1: 0;
		decimal coupaRushS = 0; //prcRush > 0 && !Inputs.k0day.Value? 1/dQty: 0;
		decimal coupa0DayS = 0; //prcRush > 0 &&  Inputs.k0day.Value? 1/dQty: 0;
		decimal coupaRushA = 0;
		decimal coupa0DayA = 0;
		decimal coupaSOL   = 0;
		decimal coupaBSA   = 0;
		decimal coupaSMO   = 0; //sKey=="SMO"? 1: 0;
		decimal coupaBIG   = 0; //lkpSpecs("SPEC")=="BIGSHOT"? 1: 0;
		decimal coupaTLS   = 0;
		decimal coupaPUL   = 0;
		decimal coupaUCB   = 0; //sKey=="UCB"? 1: 0;
	//-------------------------!! Update when Table is changed !!------------


	//-- Pair discountID w/ multiplier --------------------------------------
		Dictionary<string,decimal> kvp = new Dictionary<string,decimal>() {
			{dscID[0],  coupaSAI2 },
			{dscID[1],  coupaHAI2 },
			{dscID[2],  coupaNSkd },
			{dscID[3],  coupaCFFP },
			{dscID[4],  coupaLiner},
			{dscID[5],  coupaBoot },
			{dscID[6],  coupaDAJ  },
			{dscID[7],  coupaTAM  },
			{dscID[8],  coupaChip },
			{dscID[9],  top22     },
			{dscID[10], ottoWRI   },
			{dscID[11], ottoSAR   },
			{dscID[12], ottoABIL  },
			{dscID[13], ottoACT   },
			{dscID[14], ottoJPO   },
			{dscID[15], nuaeBoot  },
			{dscID[16], spineGOSS },
			{dscID[17], coupaPLI2 },
			{dscID[18], coupaART  },
			{dscID[19], coupaSSA  },
			{dscID[20], eurSTAB   },
			{dscID[21], eurLiner  },
			{dscID[22], eurNSkd   },
			{dscID[23], eurBoot   },
			{dscID[24], eurWBase  },
			{dscID[25], coupaTWMod},
			{dscID[26], coupaRushS},
			{dscID[27], coupa0DayS},
			{dscID[28], coupaRushA},
			{dscID[29], coupa0DayA},
			{dscID[30], coupaSOL  },
			{dscID[31], coupaBSA  },
			{dscID[32], coupaSMO  },
			{dscID[33], coupaBIG  },
			{dscID[34], coupaTLS  },
			{dscID[35], coupaPUL  },
			{dscID[36], coupaUCB  }
		};
	//---------------!! MAKE SURE MULTIPLIER MATCHES DISCOUNT ID ROW !!------


	//-- Loop thru arrays, add discounts on CustID, Group, ShipTo -----------
	
		if (Array.IndexOf(dscName,custID) != -1) {
			for (int i = 0; i < kvp.Count; i++){
				if (custID == dscName[i]){
					dscValCust  += kvp[dscID[i]] * dStr(dscVal[i]);
					dscRateCust += kvp[dscID[i]] * dStr(dscRate[i]);
				}
			}
		} // Customer Level Discounts

		if (Array.IndexOf(dscShip,shipTo) != -1) {
			for (int i = 0; i < kvp.Count; i++){
				if (shipTo == dscShip[i]){
					dscValShip  += kvp[dscID[i]] * dStr(dscVal[i]);
					dscRateShip += kvp[dscID[i]] * dStr(dscRate[i]);
				}
			}
		} // ShipTo Level Discounts

		if (Array.IndexOf(dscGrp,custGrp) != -1) {
			for (int i = 0; i < kvp.Count; i++){
				if (custGrp == dscGrp[i]){
					dscValGroup  += kvp[dscID[i]] * dStr(dscVal[i]);
					dscRateGroup += kvp[dscID[i]] * dStr(dscRate[i]);
				}
			}
		} // CustGroup Level Discounts

		decimal discount = dscValCust  + dscValShip  + dscValGroup;
		decimal discRate = dscRateCust + dscRateShip + dscRateGroup;
	//-------------------------------------------!! DO NOT CHANGE !!---------

	
	//-- Overwrite Discounts on Special Intra-Company Prices ----------------
		if ( custGrp=="INC" && sKey!="UCB" && sKey!="TRA" ) {

			discount = ( prcUnit - (prcBase + prcPrep) ) * 0.22m;
			discRate = 0.00m;
		}
	//--------------------------------------?? Remove Coupa Discounts ??-----


	//-- Send Pop-Up Message if Table Rows > Declared Discount Vars ---------
		if (dscID.Length > kvp.Count) 
			MessageBox.Show("New discounts not applied. Please alert an Epicor admin.");
	//-----------------------------------------------------------------------

/*==========================================================================*/



/*== SET CLIENT INPUT VALUES ===============================================*/
	// Unit Price, Price Detail, Discount ($), Discount (%);

	Inputs.dUnitPrice.Value = prcUnit;
	Inputs.eShowInfo.Value  = smo.ToString();
	Inputs.dDiscount.Value  = discount;
	Inputs.dDiscRate.Value  = discRate;
/*==========================================================================*/




/*== CHANGE LOG ==============================================================

	01/13/2023: Combine UDMethods calcComment, calcDiscount;
	01/19/2023: PcStatus ability to test INC/EUR customer pricing;

============================================================================*/

// ===========================================================================
// ChangeDate:  11/17/2021
// Description: Call External BAQ w/ Measurement parameters, return Mold IDs
// Warnings:    ## CLIENT SIDE ONLY
// FilePath:    ~/.mega/Docs/Dienen.Misc/Dev.SMO7/UDMethods/
// FileName:    SMO7-UDM_C-callSSML-v1.0.0.cs
// ===========================================================================

//Name of desired BAQ
	object[] @BAQID = new object[] {"Select-Mold-SMO"};

//Define Transaction Scope. Any text box input should work.
	var OTrans = ((InputControlValueBound<EpiTextBox, string>)Inputs["eShowInfo"].Value).Control.EpiTransaction;

//Adapt BAQ to Dynamic Query (c#), get it's parameters and types.
	string dqa = "DynamicQueryAdapter";
	string getQEPs = "GetQueryExecutionParametersbyID";
	var BAQ = Ice.Lib.Framework.AdapterHelper.GetAdapterInstance(OTrans as ILaunch, dqa);
	var ExeParm = ProcessCaller.InvokeAdapterMethod(OTrans, dqa, getQEPs, @BAQID);
	var ep = ExeParm.GetType().GetProperty("ExecutionParameter").GetValue(ExeParm, null);
	var ClearMethod = ep.GetType().GetMethod("Clear");
		ClearMethod.Invoke(ep, null);

//Method to add parameters based on our ep method
	string addEPRow = "AddExecutionParameterRow";
	var AddParamMethod = ep.GetType().GetMethod(addEPRow, new Type[] 
		{typeof(string),typeof(string),typeof(string),typeof(bool),typeof(Guid),typeof(string)});

//Define objects and send to BAQ

	Action<Object[],string,decimal> sendDecParam = (myObj,sParam,dInput) => {
		myObj = new object[] 
			{sParam, dInput.ToString(), "decimal", false, Guid.NewGuid(), "A"}; 
		AddParamMethod.Invoke(ep, myObj);
	};

	Action<Object[],string,string> sendStrParam = (myObj,sParam,sInput) => {
		myObj = new object[] 
			{sParam, sInput, "nvarchar", false, Guid.NewGuid(), "A"}; 
		AddParamMethod.Invoke(ep, myObj);
	};

	object[] @meas5 = new object[6];
	object[] @meas6 = new object[6];
	object[] @circ1 = new object[6];
	object[] @circ2 = new object[6];
	object[] @circ3 = new object[6];
	object[] @circ4 = new object[6];

	sendDecParam(@meas5, "d5Val", Inputs.d5Value.Value);
	sendDecParam(@meas6, "d6Val", Inputs.d6Value.Value);
	sendDecParam(@circ1, "d1Val", Inputs.d1Value.Value);
	sendDecParam(@circ2, "d2Val", Inputs.d2Value.Value);
	sendDecParam(@circ3, "d3Val", Inputs.d3Value.Value);
	sendDecParam(@circ4, "d4Val", Inputs.d4Value.Value);

	//Object Array to hold all Parameters
		object[] @paramsBAQ = new object[] {"Select-Mold-SMO", ExeParm};


//Call the BAQ with the defined Parameters
	var ExecuteMethod = BAQ.GetType().GetMethod("ExecuteByID", new Type[] {
		typeof(string), ExeParm.GetType()});
		ExecuteMethod.Invoke(BAQ, @paramsBAQ);
	var results = BAQ.GetType().GetProperty("QueryResults").GetValue(BAQ, null) as System.Data.DataSet;
	var ResultTable = results.Tables["Results"];

//Return the results of the BAQ for CSR and Reporting
	string MoldIDs = "AdjustedSSML_MoldID";
	StringBuilder molds = new StringBuilder();

	for (int i = 0; i < ResultTable.Rows.Count; i++) {
		string moldID = ResultTable.Rows[i][MoldIDs].ToString();
		Inputs.cMold1.Value = i==0? moldID: Inputs.cMold1.Value;
		Inputs.cMold2.Value = i==1? moldID: Inputs.cMold2.Value;
		Inputs.cMold3.Value = i==2? moldID: Inputs.cMold3.Value;
		molds.Append(moldID).AppendLine();
	}

if (ResultTable.Rows.Count == 0) {
	string noMatch = "No matching SMO molds could be found. Please send to the lab for Mold Selection.";
	MessageBox.Show(noMatch);
}


/* Syntax for adding param objects
	object[] @anyName = new object[] {
		"BAQparam",		 //Must match the param name in the BAQ
		InputVal,  	 	//The value to set that param. Must be string
		"decimal", 	 	//Param type from BAQ
		false,		    	//Leave as false
		Guid.NewGuid(), //Leave as Guid.NewGuid()
		"A"				     //Leave as "A"
	}
*/

// ===========================================================================
// ChangeDate:  05/30/2016
// Description: Generic Dynamic List: Table, Code, Display, Test, TestValue
//              Pass in Lookup Table + Code, Display, Test Columns + TestValue
//              Return a tilde delimited string for a combo box. 
//              If no test required, enter "None"/"NONE" in Test or TestValue
// FilePath:    ~/.mega/Docs/Dienen.Misc/Dev.SMO7/UDMethods/
// FileName:    SMO7-UDM_C-Combo-v1.0.0.cs
// ===========================================================================

string returnValue = "N`None - Check Table and Column Names";
int i = 0;

// Pull back data columns
string[]    CodeList = PCLookUp.DataColumnList(Table, Code).Split('~');
string[] DisplayList = PCLookUp.DataColumnList(Table, Display).Split('~');
string[]    TestList = new string[CodeList.Length];

// Check for testing required
if (Test.ToUpper() == "NONE" || TestValue.ToUpper() == "NONE") {
	
	TestValue = "NONE";
	TestList[0] = "No Test";

} else {

	TestList = PCLookUp.DataColumnList(Table, Test).Split('~');
}

// Create String
if (CodeList[0].Length >0 && TestList[0].Length >0 && DisplayList[0].Length >0) {  // Validate columns

	StringBuilder MyList = new StringBuilder();
	
	foreach (string item in CodeList) {

	  if (TestValue == "NONE" || TestList[i] == TestValue) {

	    if (DisplayList[i].ToUpper() != "ZZZ") {

	      MyList.Append(item).Append("`").Append(DisplayList[i]).Append("~");
	    } 
	  }
	  i++;
	}
	returnValue = ((MyList.ToString()).Length >0)?(MyList.ToString()).Substring(0,(MyList.ToString()).Length -1):("N`None");
}

return returnValue;

/*== dLkp-v1.1.0 =============================================================
	
	Created: 08/17/2022 -Kevin Veldman
	Changed: 09/15/2022 -KV

	Info: Return value from PcLookupTables as Decimal. Invalid returns 0.
	File: SMO-UDM_s-dLkp-v1.1.0.cs
============================================================================*/

	decimal d;
	string LkpVal = PCLookUp.DataLookup(sTable, sColumn, sRow);
	return decimal.TryParse(LkpVal, out d)? Convert.ToDecimal(LkpVal): 0;


/*== CHANGE LOG ==============================================================
	09/15/2022: Use TryParse to ensure returned string is decimal.
============================================================================*/

/*== getPriceDtl-v1.2.0 ======================================================

	Created: 12/01/2022 -Kevin Veldman
	Changed: 01/19/2023 -KV

	Info: Return all pricing/discount details as string array.
	File: SMO-UDM_s-getPriceDtl-v1.2.0.cs
============================================================================*/

/*== Global Functions & Variables ==========================================*/

	Func<string,decimal,bool> kStrDec = (s,d) => decimal.TryParse(s, out d); 
	Func<string,decimal> sDec    = s => kStrDec(s,0)? Convert.ToDecimal(s): 0;
	Func<string,bool   > kpVal   = s => ( s!="" && s.ToUpper()!="NONE" );
	Func<string,string > getSpec = s => sLkp("DefaultSpecs", s, sDevKey);
	Func<string[],string,bool> sAIO  = (a,s) => ( Array.IndexOf(a,s) >= 0 );
	Func<string,string,string> lsRow = (t,r) => PCLookUp.DataRowList(t,r);
	Func<string,string,string> lsCol = (t,c) => PCLookUp.DataColumnList(t,c);

	//-- LINQ Query to get DB Fields from Order Line ---------------------
		dynamic ld = 0;

		if (Context.Entity == "OrderDtl") {

			ld = Db.OrderDtl.Where(od => od.Company == Context.CompanyID
					&& od.OrderNum  == Context.OrderNumber
					&& od.OrderLine == Context.OrderLineNumber)
				.FirstOrDefault();

		} else if (Context.Entity == "QuoteDtl") {

			ld = Db.QuoteDtl.Where(qd => qd.Company == Context.CompanyID
					&& qd.QuoteNum  == Context.QuoteNumber 
					&& qd.QuoteLine == Context.QuoteLineNumber)
				.FirstOrDefault();
		}
	//--------------------------------------------------------------------

	int custNum = ld.CustNum;
	var custID  = Context.CustomerID;
	var custGrp = getCustGrp(ld.CustNum);
	var shipTo  = getShipTo(ld.OrderNum);
	
	decimal dQty = ld.cSBLR_c=="B"? 2: 1;
	bool kRush = ld.kRush0D_c || ld.kRush1D_c;
	bool kBoot = ld.kBoot_c && getSpec("SPEC")!="BIGSHOT";

//===========================================================================




/*== UnitPrice & Itemized OrderComment =====================================*/

	//-- Set Price & Desc Variables ------------------------------------
		StringBuilder smo = new StringBuilder();
		
		string[] Price = lsRow("PriceMaster", sDevKey).Split('~');
		string[] Desc  = lsRow("PriceMaster", "Desc" ).Split('~');

		Func<int,decimal> drPrc = i => sDec(Price[i]);
	
		decimal prcAlt  = dLkp("AltBasePrices", custGrp, sDevKey);
		decimal prcBase = prcAlt > 0? prcAlt: drPrc(3) + drPrc(4);
		decimal prcUnit = 0;

		decimal prcPrep = dLkp("Standards", "Value", "Prep") * -1;
		string  dscPrep = sLkp("Standards", "Desc" , "Prep");

		string  rushRow = (ld.kRush0D_c? "0": "1") + "RushSMO";
		decimal prcRush = ld.kRushNC_c? 0: dLkp("Standards", "Value", rushRow);
		string  dscRush = sLkp("Standards","Desc",rushRow);

		decimal dWdg = ld.dWedgeQty_c;
	//-------------------------------------------------------------------


	//-- Functions to add lines & increment price -----------------------
		Action<int,bool> addLn0 = // Single input, PartQty==OrderQty
			(iCol,Keep) => {

			if (Keep) {

				smo.Append(Desc[iCol].PadRight(28));
				smo.Append(dQty.ToString());
				smo.Append(Price[iCol].PadLeft(10));
				smo.Append((dQty*drPrc(iCol)).ToString().PadLeft(11));
				smo.AppendLine();

				prcUnit += drPrc(iCol);
			}
		};

		Action<string,bool,decimal,decimal,int> addLn1 = 
			(sDesc,Keep,Qty,dPrc,iFixed) => { //Custom Price|Qty|Desc|Fixed Params
			
			if ( Keep ) {
				
				decimal myQty = iFixed==1? Qty:(Qty*dQty);

				smo.Append( sDesc.PadRight(28) );
				smo.Append( myQty.ToString() );
				smo.Append( dPrc.ToString().PadLeft(10) );
				smo.Append( (myQty*dPrc).ToString().PadLeft(11) );
				smo.AppendLine();
				
				prcUnit += ((dPrc*Qty)/(iFixed==1? dQty: 1));
			}
		}; 
	//-------------------------------------------------------------------


	//-- Set Title + Charge + Subtotal Lines ----------------------------
		smo.Append("Description".PadRight(27));
		smo.Append("Qty");
		smo.Append("UnitPrice".PadLeft(10));
		smo.Append("ExtPrice".PadLeft(11));
		smo.AppendLine();

		addLn1(Price[0], true, 1, prcBase, 0);        // Base Device
		addLn0(11, ld.kToeWalk_c);                    // Toe-Walker Mod
		addLn0(18, ld.kSSplate_c);                    // SpringSteel Footplate
		addLn0(17, ld.kCFplate_c);                    // CarbonFiber Footplate
		addLn0(21, ld.kNonSkid_c);                    // Non-Skid Soles
		addLn0(19, ld.kDorsalChip_c);                 // Dorsal Chips
		addLn0(10, kBoot);                            // Inner Boot
		addLn0(12, ld.kLiner_c);                      // Integrated Liner
		addLn0(5,  ld.kSTPads_c);                     // ST Pads
		addLn0(6,  ld.kMetPads_c);                    // Metatarsal Pads
		addLn0(31, ld.kPlateau_c);                    // Toe Plateaus
		addLn0(32, ld.kBSHoles_c);                    // Extra Cutouts
		addLn1(Desc[33], dWdg>0, dWdg, drPrc(33), 1); // Heel Wedge
		addLn1(dscPrep, ld.kPrepped_c, 1, prcPrep, 0);// Prep Discount
		addLn1(dscRush, kRush, 1, prcRush, 1);        // Rush Charge

		smo.AppendLine();
		smo.Append("Total Price".PadRight(28));
		smo.Append(dQty.ToString());
		smo.Append(prcUnit.ToString().PadLeft(10));
		smo.Append((dQty * prcUnit).ToString().PadLeft(11));
	//-------------------------------------------------------------------


	/*-- PriceMaster Table Column-Index Reference -----------------------
		0	Desc1
		1	HCPSC
		2	Code
		3	BASE
		4	STD ADD
		5	STPad
		6	MetPad
		7	tlsoGills
		8	tlsoGusset
		9	tlsoKIEU
		10	kInnerBootOpt
		11	kToeWalk
		12	kLinerOpt
		13	kTStrap
		14	kAntShellOpt
		15	kAntShellOpt2
		16	kDiabeticInsert
		17	kCarbonPlate
		18	kSpringPlate
		19	kDorsalChips
		20	kCarbonSupport
		21	kNonSkid
		22	kWalkingBase
		23	kNightStretch
		24	kQuickRelease
		25	kPowderCoat
		26	kUpgrade
		27	CarbonFiberShank
		28	RGOBucket
		29	HKAFOPelvicBand
		30	AFOFCLift
		31	kPlateau
		32	kBigShotHoles
		33	kWedge
		34	kExtraMods
		35	kAppScanFee 
	-------------------------------------------------------------------*/

/*==========================================================================*/





/*== Discounts on Configured Pricing =======================================*/

	//-- Local Functions & Variables ----------------------------------------
		string[]   dscID = lsCol("uniqueDiscounts", "discountID").Split('~');
		string[] dscShip = lsCol("uniqueDiscounts", "shipToID"  ).Split('~');
		string[] dscName = lsCol("uniqueDiscounts", "custID"    ).Split('~');
		string[]  dscGrp = lsCol("uniqueDiscounts", "custGrp"   ).Split('~');
		string[]  dscVal = lsCol("uniqueDiscounts", "discAmnt"  ).Split('~');
		string[] dscRate = lsCol("uniqueDiscounts", "discRate"  ).Split('~');

		string  myCustID = Context.CustomerID;
		string myCustGrp = custGrp;
		string  myShipTo = getShipTo(ld.CustNum);

		decimal dscValCust=0,  dscValShip=0,  dscValGroup=0;
		decimal dscRateCust=0, dscRateShip=0, dscRateGroup=0;
	//-------------------------------------------!! DO NOT CHANGE !!---------


	//-- Set Condition/Multiplier for each discountID -----------------------
		decimal coupaSAI2  = 0;
		decimal coupaHAI2  = 0;
		decimal coupaNSkd  = 0; // ld.kNonSkid_c? 1: 0;
		decimal coupaCFFP  = 0; // ld.kCFplate_c? 1: 0;
		decimal coupaLiner = 0; // ld.kLiner_c? 1: 0;
		decimal coupaBoot  = 0; // kBoot? 1: 0;
		decimal coupaDAJ   = 0;
		decimal coupaTAM   = 0;
		decimal coupaChip  = 0; // ld.kDorsalChip_c && getSpec("ChipsIncluded")!="X"? 1: 0;
		decimal top22      = 1;
		decimal ottoWRI    = 1;  // All Ottobock orders 5% discount
		decimal ottoSAR    = 1;  // All Ottobock orders 5% discount
		decimal ottoABIL   = 1;  // All Ottobock orders 5% discount 
		decimal ottoACT    = 1;  // All Ottobock orders 5% discount
		decimal ottoJPO    = 1;  // All Ottobock orders 5% discount
		decimal nuaeBoot   = kBoot? 1: 0;
		decimal spineGOSS  = 0; 
		decimal coupaPLI2  = 0;
		decimal coupaART   = 0;
		decimal coupaSSA   = 0;
		decimal eurSTAB    = 0; 
		decimal eurLiner   = ld.kLiner_c? 1: 0;
		decimal eurNSkd    = ld.kNonSkid_c? 1: 0;
		decimal eurBoot    = kBoot? 1: 0;
		decimal eurWBase   = 0;
		decimal coupaTWMod = 0; // ld.kToeWalk_c? 1: 0;
		decimal coupaRushS = 0; // kRush && !ld.kRush0D_c && !ld.kRushNC_c? 1/dQty: 0;
		decimal coupa0DayS = 0; // ld.kRush0D_c && !ld.kRushNC_c? 1/dQty: 0;
		decimal coupaRushA = 0;
		decimal coupa0DayA = 0;
		decimal coupaSOL   = 0;
		decimal coupaBSA   = 0;
		decimal coupaSMO   = 0; // sDevKey=="SMO"? 1: 0;
		decimal coupaBIG   = 0; // getSpec("SPEC")=="BIGSHOT"? 1: 0;
		decimal coupaTLS   = 0;
		decimal coupaPUL   = 0;
		decimal coupaUCB   = 0; // sDevKey=="UCB"? 1: 0;
	//-------------------------!! Coupa Discounts Removed !!-----------------


	//-- Pair discountID w/ multiplier --------------------------------------
		Dictionary<string,decimal> kvp = new Dictionary<string,decimal>() {
			{dscID[0],  coupaSAI2 },
			{dscID[1],  coupaHAI2 },
			{dscID[2],  coupaNSkd },
			{dscID[3],  coupaCFFP },
			{dscID[4],  coupaLiner},
			{dscID[5],  coupaBoot },
			{dscID[6],  coupaDAJ  },
			{dscID[7],  coupaTAM  },
			{dscID[8],  coupaChip },
			{dscID[9],  top22     },
			{dscID[10], ottoWRI   },
			{dscID[11], ottoSAR   },
			{dscID[12], ottoABIL  },
			{dscID[13], ottoACT   },
			{dscID[14], ottoJPO   },
			{dscID[15], nuaeBoot  },
			{dscID[16], spineGOSS },
			{dscID[17], coupaPLI2 },
			{dscID[18], coupaART  },
			{dscID[19], coupaSSA  },
			{dscID[20], eurSTAB   },
			{dscID[21], eurLiner  },
			{dscID[22], eurNSkd   },
			{dscID[23], eurBoot   },
			{dscID[24], eurWBase  },
			{dscID[25], coupaTWMod},
			{dscID[26], coupaRushS},
			{dscID[27], coupa0DayS},
			{dscID[28], coupaRushA},
			{dscID[29], coupa0DayA},
			{dscID[30], coupaSOL  },
			{dscID[31], coupaBSA  },
			{dscID[32], coupaSMO  },
			{dscID[33], coupaBIG  },
			{dscID[34], coupaTLS  },
			{dscID[35], coupaPUL  },
			{dscID[36], coupaUCB  }
		}; 
	//---------------!! MAKE SURE MULTIPLIER MATCHES DISCOUNT ID ROW !!------


	//-- Loop thru arrays, add discounts on CustID, Group, ShipTo -----------

		if ( sAIO(dscName,custID) ) {
			for (int i = 0; i < kvp.Count; i++){
				if (custID == dscName[i]){
					dscValCust  += kvp[dscID[i]] * sDec(dscVal[i]);
					dscRateCust += kvp[dscID[i]] * sDec(dscRate[i]);
				}
			}
		} // Customer Level Discounts

		if ( sAIO(dscShip,shipTo) ) {
			for (int i = 0; i < kvp.Count; i++){
				if (shipTo == dscShip[i]){
					dscValShip  += kvp[dscID[i]] * sDec(dscVal[i]);
					dscRateShip += kvp[dscID[i]] * sDec(dscRate[i]);
				}
			}
		} // ShipTo Level Discounts

		if ( sAIO(dscGrp,custGrp) ) {
			for (int i = 0; i < kvp.Count; i++){
				if (custGrp == dscGrp[i]){
					dscValGroup  += kvp[dscID[i]] * sDec(dscVal[i]);
					dscRateGroup += kvp[dscID[i]] * sDec(dscRate[i]);
				}
			}
		} // CustGroup Level Discounts

		decimal discount = dscValCust  + dscValShip  + dscValGroup;
		decimal discRate = dscRateCust + dscRateShip + dscRateGroup;
	//-------------------------------------------!! DO NOT CHANGE !!---------


	//-- Overwrite Discounts on Special Intra-Company Prices ----------------
		if ( custGrp=="INC" && sDevKey!="UCB" && sDevKey!="TRA" ) {

			discount = ( prcUnit - (prcBase + prcPrep) ) * 0.22m;
			discRate = 0.00m;
		}
	//---------------------------------------- Removed Coupa Discounts ------

/*==========================================================================*/





/*== SET RETURN ARRAY ======================================================*/
	// UnitPrice[0], Pricing Detail[1], Discount[2], Discount Rate[3]

	string[] returnVals = {
		prcUnit.ToString(), 
		smo.ToString(), 
		discount.ToString(), 
		discRate.ToString()
	};

	return returnVals;

/*==========================================================================*/




/*== CHANGE LOG ==============================================================
	11/30/2022: Combined UDMethods getPriceCom, getDiscounts;
	01/05/2023: Add overwrite for new Hanger Discounts; Minor refactoring;
	01/19/2023: Old COUPA Discounts Removed;
