		4   bit  kPrepped_c
		5   bit  kNonSkid_c
		6   bit  kCFplate_c
		7   bit  kSSplate_c
		8   dec  dMeas5_c
		9   bit  kLiner_c
		10  int  OrderNum
		11  bit  kRush0D_c
		12  bit  kRush1D_c
		13  str  cPattern_c
		14  bit  kTallEars_c
		15  bit  kDorsalChip_c
		16  bit  kMetPads_c
		17  bit  kSTPads_c
		18  str  cSBLR_c
		19  bit  kPlateau_c
		20  dec  dMeas6_c
		21  str  cPlasticDuro_c
		22  str  cPlasticType_c
		23  str  cStrapColor_c
		24  str  cBoot_c
		25  str  cChafeType_c
		26  str  cShoeSize_c
		27  bit  kToeWalk_c
