		Sample:
			"SMO~M~0~0~0~0~0~0~2.5~0~1491481~0~0~PAT-AFO-BASEBALL~0~1~0~0~B~0~0.00~116~SSW~White~OPTEK332~DA~CUST~0~"
	<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<*/

StringBuilder pn = new StringBuilder();

pn.Append(lkpSpecs("HCPCSCODE")).Append("-");
pn.Append(lkpSpecs("PARTCODE")).Append("-");
pn.Append(lkpSpecs("BRAND")=="SS"? "SS": "CF");

Inputs.cQuotePN.Value = pn.ToString();

/*== setMeasVals-v2.2.0 ======================================================

	Created: 08/18/2022 -Kevin Veldman
	Changed: 01/13/2023 -Kevin Veldman

