// ===========================================================================
// ChangeDate:  08/03/2022
// Description: Set dtPromDate and dtTurnDate based on Input Values
// FilePath:    ~/.mega/Docs/Dienen.Misc/Dev.SMO7/UDMethods/
// FileName:    SMO7-UDM_C-setTurnDate-v1.0.0.cs
// ===========================================================================

Inputs.kModTime.Value = Inputs.rCastMeas.Value!="M";

if (Inputs.bStyle.Value=="SMO" || Inputs.bStyle.Value=="TRA") {
	Inputs.kBootTime.Value = Inputs.kInnerBoot.Value;
}

/* REPLACED ABOVE
setTurnMods(); */

DateTime turnDate = Convert.ToDateTime(Inputs.dtProdStart.Value);
string[] arrHols = PCLookUp.DataColumnList("holidays","holidays").Split('~');
int iProm = Convert.ToInt32(PCLookUp.DataLookup("Standards","Value","PromiseDays"));

Action<int> plusDays = (iDays) => {

	turnDate = turnDate.AddDays(iDays);

	for (int i = 0; i < arrHols.Length; i++) {

		if (arrHols[i] == turnDate.ToString("yyyyMMdd")) {

			turnDate = turnDate.AddDays(1);
		}
	}
	switch (turnDate.DayOfWeek) {

		case DayOfWeek.Friday:
			turnDate = turnDate.AddDays(3);
			break;

		case DayOfWeek.Saturday:
			turnDate = turnDate.AddDays(2);
			break;

		case DayOfWeek.Sunday:
			turnDate = turnDate.AddDays(1);
			break;
	}
};
Action<int> addDays = (iDays) => {

	for (int i = 0; i < iDays; i++){
		
		plusDays(1);
		plusDays(0);
	}
};

if (!Inputs.kRush.Value) {

	int iTurns = Convert.ToInt32(lkpSpecs("TurnDays"));

	iTurns += Inputs.kModTime.Value?   2: 0;
	iTurns += Inputs.kBootTime.Value?  1: 0;
	iTurns += Inputs.kKafoTime.Value?  1: 0;
	iTurns += Inputs.kShellTime.Value? 1: 0;

	addDays(iTurns);

	Inputs.dtTurnDate.Value = turnDate;

	iProm += (Inputs.bStyle.Value == "SMO" && !Inputs.kModTime.Value && iProm > 2)? 2-iProm: 0;
	iProm += Inputs.kModTime.Value? 1: 0;

	addDays(iProm);

	Inputs.dtPromised.Value = turnDate;

} else {

	int iRush = Inputs.k0day.Value? 0: 1;

	addDays(iRush);

	Inputs.dtTurnDate.Value = turnDate;
	Inputs.dtPromised.Value = turnDate;	
}

/*============================================================================
	Method: SMO-UDM_c-setUOM-v1.0.0.cs

