string updateTtl = "Update Production Schedule";
string updateMsg = "SS Turnaround date will be set based on today instead of the OrderDate. Continue?";
string cancelMsg = "Canceled, Turnaround date set based on OrderDate.";

Func<string,string,DialogResult> mbYN = (sM,sT) => {
    var tmp = MessageBoxIcon.Question;
    return MessageBox.Show(sM,sT,MessageBoxButtons.YesNo,tmp);
};
Func<DateTime?,string> mbNewTdt = (turnDate) => {
	string sTmp = "New Turnaround Date: ";
	DateTime dtTmp = Convert.ToDateTime(turnDate);
	return sTmp + dtTmp.ToString("MM/dd/yy");
};

DialogResult diResult0 = mbYN(updateMsg,updateTtl);

if (diResult0 == DialogResult.No) {
	Inputs.dtProdStart.Value = Inputs.dtOrderDate.Value;
	MessageBox.Show(cancelMsg);
} else {
  Inputs.dtProdStart.Value = DateTime.Today;
