string myMold = Inputs.cMold1.Value;
string badID = "Molds should begin 'S-' or 'A-' followed by a number between 1 and 999. Please double-check your mold ID.";
int moldNum;
bool isNumber = int.TryParse(myMold, out moldNum);

if (isNumber) {

	if (moldNum > 999 || moldNum < 1) {
		MessageBox.Show("Invalid Mold Number");
	} 

	Inputs.cMold1.Value = "S-" + myMold;

	if (Inputs.cMold1.Value.Length > 5) {
